void swap(vector& arr, int i, int j)
{
    int tmp = arr[i];
    arr[i] = arr[j];
    arr[j] = tmp;
}

vector partiton(vector& arr, int l, int r)    //荷兰国旗问题
{
    int less = l - 1;
    int more = r;
    while (l arr[r])
        {
            swap(arr, --more, l);
        }
        else
        {
            l++;
        }
    }
    swap(arr, more, r);            //默认以arr[r]做划分，不可遗漏最后一位数！
    return { less + 1,more };
}

void quickSort(vector& arr, int l, int r)        //改进的快排，随机取数字做划分值
